import React, { useState, useEffect } from 'react';
import '../style/stopwatch.css'; // Adjust the path based on your folder structure

const Stopwatch = () => {
  const [time, setTime] = useState(0);
  const [isActive, setIsActive] = useState(false);

  // Effect to handle the stopwatch timing
  useEffect(() => {
    let interval = null;
    if (isActive) {
      interval = setInterval(() => {
        setTime((prevTime) => prevTime + 10);
      }, 10);
    }
    return () => clearInterval(interval);
  }, [isActive]);

  // Start the stopwatch
  const handleStart = () => {
    setIsActive(true);
  };

  // Stop the stopwatch
  const handleStop = () => {
    setIsActive(false);
  };

  // Reset the stopwatch
  const handleReset = () => {
    setIsActive(false);
    setTime(0);
  };

  // Format time into mm:ss:ms
  const formatTime = (time) => {
    const minutes = String(Math.floor((time / 60000) % 60)).padStart(2, '0');
    const seconds = String(Math.floor((time / 1000) % 60)).padStart(2, '0');
    const milliseconds = String(Math.floor((time % 1000) / 10)).padStart(2, '0');
    return `${minutes}:${seconds}:${milliseconds}`;
  };

  return (
    <div className="container">
      <h1>{formatTime(time)}</h1>
      <div className="buttonContainer">
        <Button onClick={handleStart} text="Start" />
        <Button onClick={handleStop} text="Stop" />
        <Button onClick={handleReset} text="Reset" />
      </div>
    </div>
  );
};

// Button component to encapsulate button styling and behavior
const Button = ({ onClick, text }) => (
  <button onClick={onClick} className="button">
    {text}
  </button>
);

export default Stopwatch;
