import React, { useState, useEffect } from 'react';
import '../style/stopwatch.css';
import flagImage from '../images/flag.png'; // Adjust the path as necessary

const Stopwatch = () => {
  const [time, setTime] = useState(0);
  const [isActive, setIsActive] = useState(false);

  useEffect(() => {
    let interval = null;
    if (isActive) {
      interval = setInterval(() => {
        setTime(prevTime => prevTime + 10);
      }, 10);
    } else if (!isActive && time !== 0) {
      clearInterval(interval);
    }
    return () => clearInterval(interval);
  }, [isActive, time]);

  const handleStart = () => {
    setIsActive(true);
  };

  const handleStop = () => {
    setIsActive(false);
  };

  const handleReset = () => {
    setIsActive(false);
    setTime(0);
  };

  const formatTime = (time) => {
    const minutes = Math.floor((time / 60000) % 60);
    const seconds = Math.floor((time / 1000) % 60);
    const milliseconds = Math.floor((time % 1000) / 10);
    return `${String(minutes).padStart(2, '0')}:${String(seconds).padStart(2, '0')}:${String(milliseconds).padStart(2, '0')}`;
  };

  return (
    <div className="container">
      <h1>
        <img src={flagImage} alt="Flag" style={{ width: '30px', marginRight: '10px' }} />
        {formatTime(time)}
      </h1>
      <div className="buttonContainer">
        <button onClick={handleStart} className="button">Start</button>
        <button onClick={handleStop} className="button">Stop</button>
        <button onClick={handleReset} className="button">Reset</button>
      </div>
    </div>
  );
};

export default Stopwatch;
