import React, { useState, useEffect } from "react";
import "./../style/alarm.css";

const Alarm = () => {
  const [alarmTime, setAlarmTime] = useState("");
  const [alarms, setAlarms] = useState([]); // Array to hold all alarms
  const [currentTime, setCurrentTime] = useState(new Date());
  const [alarmMessage, setAlarmMessage] = useState("");
  const [showForm, setShowForm] = useState(false);
  const [isSvgClicked, setIsSvgClicked] = useState(false);

  useEffect(() => {
    const interval = setInterval(() => {
      const now = new Date();
      setCurrentTime(now);
      checkAlarms(now); // Pass current time to check alarms
    }, 1000);

    return () => clearInterval(interval);
  }, [alarms]); // Only depend on alarms

  const checkAlarms = (now) => {
    setAlarms((prevAlarms) =>
      prevAlarms.map((alarm) => {
        const [alarmHours, alarmMinutes] = alarm.time24.split(":").map(Number); // Use 24-hour format for comparison
        const alarmDate = new Date(now.getFullYear(), now.getMonth(), now.getDate(), alarmHours, alarmMinutes);

        if (alarmDate < now) {
          alarmDate.setDate(alarmDate.getDate() + 1); // Set for the next day if time has passed
        }

        // Check if the alarm should ring
        if (
          now.getHours() === alarmHours &&
          now.getMinutes() === alarmMinutes &&
          alarm.isActive
        ) {
          // Alarm is ringing, disable it
          setAlarmMessage(`Alarm ringing for ${alarm.time12}!`);
          return { ...alarm, isActive: false }; // Turn off the alarm after it rings
        }

        return alarm; // Return unchanged if the alarm shouldn't ring yet
      })
    );

    // Clear the alarm message after 5 seconds
    setTimeout(() => {
      setAlarmMessage("");
    }, 5000);
  };

  const handleSetAlarm = () => {
    if (alarmTime) {
      const newAlarm = {
        time24: alarmTime, // Store the time in 24-hour format
        time12: formatAlarmTime(alarmTime), // Display in 12-hour format
        id: Date.now(),
        isActive: true,
      };
      setAlarms((prev) => [...prev, newAlarm]);
      setAlarmMessage(`Alarm set for ${newAlarm.time12}`); // Show the formatted time in the message
      setAlarmTime("");

      // Clear the alarm message after 3 seconds
      setTimeout(() => {
        setAlarmMessage("");
      }, 3000);

      // Hide the form after setting the alarm
      setShowForm(false);
    } else {
      setAlarmMessage("Please enter a valid time");
    }
  };

  // Function to format time to 12-hour format with AM/PM
  const formatAlarmTime = (time) => {
    const [hours, minutes] = time.split(":").map(Number);
    const ampm = hours >= 12 ? "PM" : "AM";
    const formattedHours = hours % 12 || 12; // Convert 0 to 12 for midnight
    return `${formattedHours}:${minutes < 10 ? "0" + minutes : minutes} ${ampm}`;
  };

  const calculateRemainingTime = (alarm) => {
    const [alarmHours, alarmMinutes] = alarm.time24.split(":").map(Number); // Use 24-hour format for calculation
    const now = new Date();
    const alarmDate = new Date(
      now.getFullYear(),
      now.getMonth(),
      now.getDate(),
      alarmHours,
      alarmMinutes
    );

    // Adjust for next day if the alarm time is in the past
    if (alarmDate < now) {
      alarmDate.setDate(alarmDate.getDate() + 1);
    }

    const diff = alarmDate.getTime() - now.getTime();

    const hours = Math.floor(diff / (1000 * 60 * 60));
    const minutes = Math.floor((diff % (1000 * 60 * 60)) / (1000 * 60));
    const seconds = Math.floor((diff % (1000 * 60)) / 1000);

    return `Alarm in ${hours}h ${minutes}m ${seconds}s`; // Format the remaining time
  };

  // Function to calculate time remaining in milliseconds
  const calculateTimeRemainingMs = (alarm) => {
    const [alarmHours, alarmMinutes] = alarm.time24.split(":").map(Number);
    const now = new Date();
    const alarmDate = new Date(
      now.getFullYear(),
      now.getMonth(),
      now.getDate(),
      alarmHours,
      alarmMinutes
    );

    if (alarmDate < now) {
      alarmDate.setDate(alarmDate.getDate() + 1);
    }

    return alarmDate.getTime() - now.getTime();
  };

  const toggleAlarm = (id) => {
    setAlarms((prev) =>
      prev.map((alarm) => {
        if (alarm.id === id) {
          const newStatus = !alarm.isActive;
          if (newStatus) {
            setAlarmMessage(`Alarm for ${alarm.time12} reactivated!`);

            // Clear the notification after 3 seconds
            setTimeout(() => {
              setAlarmMessage("");
            }, 3000);
          }
          return { ...alarm, isActive: newStatus };
        }
        return alarm;
      })
    );
  };

  // Handle the click on the SVG to show the form
  const handleSvgClick = () => {
    setShowForm(true); // Show the form every time the SVG is clicked
    setIsSvgClicked(true); // Set to true after the first click
  };

  // Sort alarms based on remaining time before rendering
  const sortedAlarms = [...alarms].sort(
    (a, b) => calculateTimeRemainingMs(a) - calculateTimeRemainingMs(b)
  );

  return (
    <div className="container">
      {alarmMessage && <div className="notification">{alarmMessage}</div>}

      {/* Hide the Alarm Clock text after the first SVG click */}
      {!isSvgClicked && <h2>Alarm Clock</h2>}

      <div className="timeDisplay">
        <h3>
          {currentTime.toLocaleTimeString([], {
            hour: "2-digit",
            minute: "2-digit",
            second: "2-digit",
            hour12: true,
          })}
        </h3>

        {/* Always show SVG without tooltip */}
        <div
          onClick={handleSvgClick}
          style={{ cursor: "pointer", position: "relative" }}
        >
          <svg
            width="50px"
            height="50px"
            viewBox="0 0 24 24"
            fill="none"
            xmlns="http://www.w3.org/2000/svg"
          >
            <g id="SVGRepo_bgCarrier" strokeWidth="0"></g>
            <g
              id="SVGRepo_tracerCarrier"
              strokeLinecap="round"
              strokeLinejoin="round"
            ></g>
            <g id="SVGRepo_iconCarrier">
              <path
                d="M11 8C11 7.44772 11.4477 7 12 7C12.5523 7 13 7.44772 13 8V11H16C16.5523 11 17 11.4477 17 12C17 12.5523 16.5523 13 16 13H13V16C13 16.5523 12.5523 17 12 17C11.4477 17 11 16.5523 11 16V13H8C7.44771 13 7 12.5523 7 12C7 11.4477 7.44772 11 8 11H11V8Z"
                fill="#0F0F0F"
              ></path>
              <path
                fillRule="evenodd"
                clipRule="evenodd"
                d="M23 12C23 18.0751 18.0751 23 12 23C5.92487 23 1 18.0751 1 12C1 5.92487 5.92487 1 12 1C18.0751 1 23 5.92487 23 12ZM3.00683 12C3.00683 16.9668 7.03321 20.9932 12 20.9932C16.9668 20.9932 20.9932 16.9668 20.9932 12C20.9932 7.03321 16.9668 3.00683 12 3.00683C7.03321 3.00683 3.00683 7.03321 3.00683 12Z"
                fill="#0F0F0F"
              ></path>
            </g>
          </svg>
        </div>

        {/* Show alarm form only if the SVG is clicked */}
        {showForm && (
          <div>
            <input
              type="time"
              value={alarmTime}
              onChange={(e) => setAlarmTime(e.target.value)}
              className="input"
            />
            <button onClick={handleSetAlarm} className="button">
              Set Alarm
            </button>
          </div>
        )}

        {sortedAlarms.length > 0 && (
          <div>
            <ul>
              {sortedAlarms.map((alarm) => (
                <li key={alarm.id} className="glassy-effect">
                  {alarm.time12} -{" "}
                  {alarm.isActive
                    ? calculateRemainingTime(alarm)
                    : "Alarm Disabled"}
                  <label className="switch" style={{ marginLeft: "10px", fontSize: "0.8em" }}>
                    <input
                      type="checkbox"
                      checked={alarm.isActive}
                      onChange={() => toggleAlarm(alarm.id)}
                      style={{ transform: "scale(0.8)" }} // Adjust slider size
                    />
                    <span
                      className={`slider ${alarm.isActive ? "" : "round"}`}
                      style={{ width: "49px", height: "21px" }} // Adjust slider size
                    ></span>
                  </label>
                </li>
              ))}
            </ul>
          </div>
        )}
      </div>
    </div>
  );
};

export default Alarm;
