import React, { useState, useEffect } from "react";
import "./../style/alarm.css"; // Ensure your CSS file is correctly linked
import add from "./../img/add.svg"; // Import your new SVG
import Delete from "./../img/delete.svg"; // Import your delete SVG
import alarm from "./../img/alarm-plus-svgrepo-com.svg"; // Import your alarm SVG
import sound from "./../audio/reminder.mp3"

const Alarm = () => {
  const [alarmTime, setAlarmTime] = useState("");
  const [alarms, setAlarms] = useState([]);
  const [currentTime, setCurrentTime] = useState(new Date());
  const [alarmMessage, setAlarmMessage] = useState("");
  const [showForm, setShowForm] = useState(false);
  const [showCheckboxes, setShowCheckboxes] = useState(false);
  const [selectedAlarms, setSelectedAlarms] = useState({});
  const [showDeleteButton, setShowDeleteButton] = useState(false);
  const [showAlarmClock, setShowAlarmClock] = useState(false);
  const [ringingAlarmId, setRingingAlarmId] = useState(null);
  const audio = new Audio(sound);

  useEffect(() => {
    const interval = setInterval(() => {
      const now = new Date();
      setCurrentTime(now);
      checkAlarms(now);
    }, 1000);

    return () => clearInterval(interval);
  }, [alarms]);

  const stopAlarm = () => {
    setAlarms((prevAlarms) =>
      prevAlarms.map((alarm) => {
        if (alarm.id === ringingAlarmId) {
          return { ...alarm, isActive: false };
        }
        return alarm;
      })
    );
    setRingingAlarmId(null);
  };

  const snoozeAlarm = () => {
    setAlarms((prevAlarms) =>
      prevAlarms.map((alarm) => {
        if (alarm.id === ringingAlarmId) {
          const [hours, minutes] = alarm.time24.split(":").map(Number);
          const snoozedTime = new Date();
          snoozedTime.setHours(hours);
          snoozedTime.setMinutes(minutes + 5);
          const newTime24 = `${snoozedTime.getHours().toString().padStart(2, "0")}:${snoozedTime.getMinutes().toString().padStart(2, "0")}`;
          return { ...alarm, time24: newTime24 };
        }
        return alarm;
      })
    );
    setRingingAlarmId(null);
  };

  const checkAlarms = (now) => {
    setAlarms((prevAlarms) =>
      prevAlarms.map((alarm) => {
        const [alarmHours, alarmMinutes] = alarm.time24.split(":").map(Number);
        if (now.getHours() === alarmHours && now.getMinutes() === alarmMinutes && alarm.isActive) {
          setAlarmMessage(`Alarm ringing for ${alarm.time12}!`);
          setRingingAlarmId(alarm.id);
          audio.play();
          return { ...alarm, isActive: false };
        }
        return alarm;
      })
    );

    setTimeout(() => {
      setAlarmMessage("");
    }, 5000);
  };

  const handleSetAlarm = () => {
    if (alarmTime) {
      const newAlarm = {
        time24: alarmTime,
        time12: formatAlarmTime(alarmTime),
        id: Date.now(),
        isActive: true,
      };
      setAlarms((prev) => [...prev, newAlarm]);
      setAlarmMessage(`Alarm set for ${newAlarm.time12}`);
      setAlarmTime("");
      setShowForm(false);
      setShowDeleteButton(true);

      setTimeout(() => {
        setAlarmMessage("");
      }, 3000);
    } else {
      setAlarmMessage("Please enter a valid time");
      setTimeout(() => {
        setAlarmMessage("");
      }, 3000);
    }
  };

  const formatAlarmTime = (time) => {
    const [hours, minutes] = time.split(":").map(Number);
    const ampm = hours >= 12 ? "PM" : "AM";
    const formattedHours = hours % 12 || 12;
    return `${formattedHours}:${minutes < 10 ? "0" + minutes : minutes} ${ampm}`;
  };

  const toggleCheckbox = (id) => {
    setSelectedAlarms((prev) => ({
      ...prev,
      [id]: !prev[id],
    }));
  };

  const deleteSelectedAlarms = () => {
    const deletedAlarms = alarms.filter((alarm) => selectedAlarms[alarm.id]);
    setAlarms((prev) => prev.filter((alarm) => !selectedAlarms[alarm.id]));
    setSelectedAlarms({});
    setShowCheckboxes(false);

    if (deletedAlarms.length > 0) {
      setAlarmMessage(`Deleted ${deletedAlarms.length} selected alarm(s).`);
    } else {
      setAlarmMessage("No alarms were selected for deletion.");
    }

    setTimeout(() => {
      setAlarmMessage("");
    }, 5000);
  };

  const handleDeleteClick = () => {
    if (alarms.length === 0) {
      setAlarmMessage("No alarms were set for deletion.");
      setTimeout(() => {
        setAlarmMessage("");
      }, 3000);
    } else if (showCheckboxes) {
      deleteSelectedAlarms();
    } else {
      setShowCheckboxes(true);
    }
  };

  const sortedAlarms = [...alarms].sort(
    (a, b) => calculateRemainingTime(a) - calculateRemainingTime(b)
  );

  return (
    <div className="container">
      {alarmMessage && <div className="notification">{alarmMessage}</div>}
      {ringingAlarmId && (
        <div className="notification">
          <p>Alarm ringing!</p>
          <button onClick={stopAlarm}>Stop</button>
          <button onClick={snoozeAlarm}>Snooze</button>
        </div>
      )}

      <div className="timeDisplay">
        {showAlarmClock && (
          <>
            <h4>{currentTime.toLocaleTimeString([], { hour: "2-digit", minute: "2-digit", second: "2-digit", hour12: true })}</h4>
            <div style={{ display: "flex", gap: "10px", justifyContent: showForm || (showDeleteButton && alarms.length > 0) ? "space-between" : "center" }}>
              {!showForm && (
                <div onClick={() => { setShowForm((prev) => !prev); setShowDeleteButton(false); }} style={{ cursor: "pointer" }} title="Add Alarm">
                  <img src={add} alt="Add Alarm" width="50" height="50" />
                </div>
              )}
              {showDeleteButton && alarms.length > 0 && (
                <div onClick={handleDeleteClick} style={{ cursor: "pointer" }} title="Delete Selected Alarms">
                  <img src={Delete} alt="Delete Selected Alarms" width="50" height="50" />
                </div>
              )}
            </div>
            {showForm && (
              <div className="button-container">
                <input type="time" value={alarmTime} onChange={(e) => setAlarmTime(e.target.value)} className="input" />
                <button onClick={handleSetAlarm} className="button">Set Alarm</button>
              </div>
            )}
            {sortedAlarms.length > 0 && (
              <div className="alarm-list">
                <ul>
                  {sortedAlarms.map((alarm) => (
                    <li key={alarm.id} className="glassy-effect">
                      <div>{alarm.time12}</div>
                      <button onClick={() => toggleCheckbox(alarm.id)}>Select</button>
                    </li>
                  ))}
                </ul>
              </div>
            )}
          </>
        )}
      </div>
    </div>
  );
};

export default Alarm;
