import React, { useState, useEffect } from "react";
import Select from "react-select";
import './../style/timezonepicker.css';

const TimeZonePicker = () => {
  const [timeZones, setTimeZones] = useState([]);
  const [selectedTimeZone, setSelectedTimeZone] = useState("");
  const [timeDifference, setTimeDifference] = useState("00:00:00"); // Initialize time difference
  const [selectedLocationTime, setSelectedLocationTime] = useState("00:00:00"); // Initialize selected location time
  const [currentLocationTime, setCurrentLocationTime] = useState("00:00:00"); // Initialize current location time
  const [isAheadOrBehind, setIsAheadOrBehind] = useState(""); // State to track if selected time zone is ahead or behind

  useEffect(() => {
    const getTimeZones = () => {
      try {
        return Intl.supportedValuesOf("timeZone");
      } catch (err) {
        return [
          "America/New_York",
          "Europe/London",
          "Asia/Tokyo",
          "Australia/Sydney",
          "UTC",
        ];
      }
    };

    // Get the supported time zones and map them
    const supportedZones = getTimeZones().map((zone) => ({
      value: zone,
      label: zone,
    }));

    // Set time zones to state
    setTimeZones(supportedZones);
  }, []);

  useEffect(() => {
    const updateTime = () => {
      if (selectedTimeZone) {
        const now = new Date();

        // Get the current location time
        const currentOptions = {
          timeZone: Intl.DateTimeFormat().resolvedOptions().timeZone,
        };
        const currentTime = now.toLocaleString("en-US", currentOptions);

        // Get the selected time zone time
        const selectedOptions = {
          timeZone: selectedTimeZone,
        };
        const selectedTime = now.toLocaleString("en-US", selectedOptions);

        // Calculate time difference in seconds
        const currentDateTime = new Date(currentTime).getTime();
        const selectedDateTime = new Date(selectedTime).getTime();
        const differenceInSeconds = Math.floor((selectedDateTime - currentDateTime) / 1000);

        // Determine if current time is ahead or behind
        if (differenceInSeconds > 0) {
          setIsAheadOrBehind("ahead of the current location");
        } else if (differenceInSeconds < 0) {
          setIsAheadOrBehind("behind the current location");
        } else {
          setIsAheadOrBehind("Current location and selected location are at the same time");
        }
      }
    };

    const interval = setInterval(updateTime, 1000);
    return () => clearInterval(interval);
  }, [selectedTimeZone]);


  const handleTimeZoneChange = (selectedOption) => {
    setSelectedTimeZone(selectedOption.value);
  };

  return (
    <div className="timezone-picker-container">
      <label htmlFor="timezone">Select Time Zone:</label>
      <Select
        id="timezone"
        options={timeZones}
        onChange={handleTimeZoneChange}
        placeholder="Search and select a time zone"
      />
      {selectedTimeZone && (
        <div>
          <h3>{selectedTimeZone}</h3>
          <p>{selectedLocationTime}</p>
          <p>{currentLocationTime}</p>
          <p>{timeDifference}</p>
          <p>{isAheadOrBehind}</p>
        </div>
      )}
    </div>
  );
};

export default TimeZonePicker;
