import React, { useState, useEffect } from 'react';
import './../style/alarm.css';

const Alarm = () => {
  const [alarmTime, setAlarmTime] = useState('');
  const [alarms, setAlarms] = useState([]); // Array to hold all alarms
  const [currentTime, setCurrentTime] = useState(new Date());
  const [alarmMessage, setAlarmMessage] = useState('');
  const [isAlarmRinging, setIsAlarmRinging] = useState(false);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentTime(new Date());
    }, 1000);

    return () => clearInterval(interval);
  }, []);

  useEffect(() => {
    checkAlarms();
  }, [currentTime, alarms]);

  const checkAlarms = () => {
    const formattedCurrentTime = currentTime.toLocaleTimeString([], {
      hour: '2-digit',
      minute: '2-digit',
      hour12: true,
    });

    alarms.forEach((alarm) => {
      if (formattedCurrentTime === alarm.time && !isAlarmRinging) {
        setIsAlarmRinging(true);
        setAlarmMessage(`Alarm ringing for ${alarm.time}!`);
        setAlarms((prev) => prev.filter(a => a.time !== alarm.time)); // Remove ringing alarm

        // Clear the alarm message after 5 seconds
        setTimeout(() => {
          setAlarmMessage('');
          setIsAlarmRinging(false);
        }, 5000);
      }
    });
  };

  const handleSetAlarm = () => {
    if (alarmTime) {
      const newAlarm = { time: formatAlarmTime(alarmTime), id: Date.now() };
      setAlarms((prev) => [...prev, newAlarm]);
      setAlarmMessage(`Alarm set for ${newAlarm.time}`);
      setAlarmTime('');

      // Clear the alarm message after 1 second
      setTimeout(() => {
        setAlarmMessage('');
      }, 1000);
    } else {
      setAlarmMessage('Please enter a valid time');
    }
  };

  const formatAlarmTime = (time) => {
    const [hours, minutes] = time.split(':');
    const date = new Date();
    date.setHours(hours);
    date.setMinutes(minutes);
    return date.toLocaleTimeString([], {
      hour: '2-digit',
      minute: '2-digit',
      hour12: true,
    });
  };

  const calculateRemainingTime = (alarm) => {
    const [alarmHours, alarmMinutes] = alarm.time.split(':');
    const now = new Date();
    const alarmDate = new Date(now.getFullYear(), now.getMonth(), now.getDate(), alarmHours, alarmMinutes);

    // If the alarm time is in the past today, set it for tomorrow
    if (alarmDate <= now) {
      alarmDate.setDate(alarmDate.getDate() + 1);
    }

    const diff = alarmDate - now; // Difference in milliseconds
    const hours = Math.floor(diff / 3600000); // Calculate hours
    const minutes = Math.floor((diff % 3600000) / 60000); // Calculate remaining minutes
    const seconds = Math.floor((diff % 60000) / 1000); // Calculate remaining seconds

    return `Alarm in ${hours}h ${minutes}m ${seconds}s`;
  };

  return (
    <div className="container">
      {alarmMessage && <div className="notification">{alarmMessage}</div>}
      <h2>Alarm Clock</h2>
      <input
        type="time"
        value={alarmTime}
        onChange={(e) => setAlarmTime(e.target.value)}
        className="input"
      />
      <button onClick={handleSetAlarm} className="button">
        Set Alarm
      </button>
      <div className="timeDisplay">
        <h3>Current Time: {currentTime.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit', second: '2-digit', hour12: true })}</h3>
        {alarms.length > 0 ? (
          <div>
            <h4>Alarms Set:</h4>
            <ul>
              {alarms.map((alarm) => (
                <li key={alarm.id}>
                  {alarm.time} - {calculateRemainingTime(alarm)}
                </li>
              ))}
            </ul>
          </div>
        ) : (
          <h4>No alarms set.</h4>
        )}
      </div>
    </div>
  );
};

export default Alarm;
