import React, { useState, useEffect } from "react";
import Select from "react-select";
import countryFlags from './../component/countryFlags'; // Ensure you have your country flags mapped correctly
import './../style/timezonepicker.css';

const TimeZonePicker = () => {
  const [timeZones, setTimeZones] = useState([]);
  const [selectedTimeZone, setSelectedTimeZone] = useState("");
  const [currentTime, setCurrentTime] = useState("");
  const [selectedCountryCode, setSelectedCountryCode] = useState(""); // State for the selected country code

  useEffect(() => {
    const getTimeZones = () => {
      try {
        return Intl.supportedValuesOf("timeZone");
      } catch (err) {
        return [
          "America/New_York",
          "Europe/London",
          "Asia/Tokyo",
          "Australia/Sydney",
          "UTC",
        ];
      }
    };

    // Get the supported time zones and map them
    const supportedZones = getTimeZones().map((zone) => ({
      value: zone,
      label: zone,
    }));

    // Set time zones to state
    setTimeZones(supportedZones);
  }, []);

  useEffect(() => {
    const updateTime = () => {
      if (selectedTimeZone) {
        const now = new Date();
        const options = {
          timeZone: selectedTimeZone,
          hour: "2-digit",
          minute: "2-digit",
          second: "2-digit",
          hour12: true,
        };
        const formatter = new Intl.DateTimeFormat([], options);
        setCurrentTime(formatter.format(now));
      }
    };

    const interval = setInterval(updateTime, 1000);
    return () => clearInterval(interval);
  }, [selectedTimeZone]);

  const handleTimeZoneChange = (selectedOption) => {
    console.log("Selected Option:", selectedOption);
    setSelectedTimeZone(selectedOption.value);
    setSelectedCountryCode(selectedOption.countryCode || ""); // Update the country code based on selected option
    console.log("Selected Country Code:", selectedOption.countryCode); // Debugging line
  };

  return (
    <div className="timezone-picker-container">
      <label htmlFor="timezone">Select Time Zone:</label>
      <Select
        id="timezone"
        options={timeZones}
        onChange={handleTimeZoneChange}
        placeholder="Search and select a time zone"
      />
      {selectedTimeZone && (
        <div>
          <h3>{selectedTimeZone}</h3>
          {selectedCountryCode && countryFlags[selectedCountryCode] && (
            <img
              src={countryFlags[selectedCountryCode]}
              alt={`${selectedCountryCode} flag`}
              style={{ width: '2em', height: '2em' }}
            />
          )}
          <p>{currentTime}</p>
        </div>
      )}
    </div>
  );
};

export default TimeZonePicker;
