import React, { useState, useEffect } from "react";
import "./../style/alarm.css"; // Ensure your CSS file is correctly linked
import add from './../img/add.svg'; // Import your new SVG
import Delete from './../img/delete.svg'; // Import your delete SVG

const Alarm = () => {
  const [alarmTime, setAlarmTime] = useState("");
  const [alarms, setAlarms] = useState([]); // Array to hold all alarms
  const [currentTime, setCurrentTime] = useState(new Date());
  const [alarmMessage, setAlarmMessage] = useState("");
  const [showForm, setShowForm] = useState(false);
  const [showCheckboxes, setShowCheckboxes] = useState(false); // New state to control checkbox display
  const [selectedAlarms, setSelectedAlarms] = useState({}); // Track selected alarms for deletion

  useEffect(() => {
    const interval = setInterval(() => {
      const now = new Date();
      setCurrentTime(now);
      checkAlarms(now); // Pass current time to check alarms
    }, 1000);

    return () => clearInterval(interval);
  }, [alarms]); // Only depend on alarms

  const checkAlarms = (now) => {
    setAlarms((prevAlarms) =>
      prevAlarms.map((alarm) => {
        const [alarmHours, alarmMinutes] = alarm.time24.split(":").map(Number);
        const alarmDate = new Date(
          now.getFullYear(),
          now.getMonth(),
          now.getDate(),
          alarmHours,
          alarmMinutes
        );

        if (alarmDate < now) {
          alarmDate.setDate(alarmDate.getDate() + 1); // Set for the next day if time has passed
        }

        // Check if the alarm should ring
        if (
          now.getHours() === alarmHours &&
          now.getMinutes() === alarmMinutes &&
          alarm.isActive
        ) {
          // Alarm is ringing, disable it
          setAlarmMessage(`Your alarm for ${alarm.time12} is ringing!`);
          return { ...alarm, isActive: false }; // Turn off the alarm after it rings
        }

        return alarm; // Return unchanged if the alarm shouldn't ring yet
      })
    );

    // Clear the alarm message after 5 seconds
    setTimeout(() => {
      setAlarmMessage("");
    }, 5000);
  };

  const handleSetAlarm = () => {
    if (alarmTime) {
      const newAlarm = {
        time24: alarmTime,
        time12: formatAlarmTime(alarmTime),
        id: Date.now(),
        isActive: true,
      };
      setAlarms((prev) => [...prev, newAlarm]);
      setAlarmMessage(`Alarm has been set for ${newAlarm.time12}.`);
      setAlarmTime("");
      setShowForm(false);

      // Clear the alarm message after 3 seconds
      setTimeout(() => {
        setAlarmMessage("");
      }, 3000);
    } else {
      setAlarmMessage("Please enter a valid time.");
      setTimeout(() => {
        setAlarmMessage("");
      }, 3000);
    }
  };

  const formatAlarmTime = (time) => {
    const [hours, minutes] = time.split(":").map(Number);
    const ampm = hours >= 12 ? "PM" : "AM";
    const formattedHours = hours % 12 || 12;
    return `${formattedHours}:${minutes < 10 ? "0" + minutes : minutes} ${ampm}`;
  };

  const calculateRemainingTime = (alarm) => {
    const [alarmHours, alarmMinutes] = alarm.time24.split(":").map(Number);
    const now = new Date();
    const alarmDate = new Date(
      now.getFullYear(),
      now.getMonth(),
      now.getDate(),
      alarmHours,
      alarmMinutes
    );

    if (alarmDate < now) {
      alarmDate.setDate(alarmDate.getDate() + 1);
    }

    return alarmDate.getTime() - now.getTime(); // Return the difference in milliseconds
  };

  const toggleAlarm = (id) => {
    setAlarms((prev) => 
      prev.map((alarm) => {
        if (alarm.id === id) {
          return { ...alarm, isActive: !alarm.isActive }; // Toggle the alarm's active state
        }
        return alarm; // Return unchanged if it's not the selected alarm
      })
    );
  };

  const toggleCheckbox = (id) => {
    setSelectedAlarms((prev) => ({
      ...prev,
      [id]: !prev[id], // Toggle the selected state for the alarm
    }));
  };

  const deleteSelectedAlarms = () => {
    setAlarms((prev) => prev.filter(alarm => !selectedAlarms[alarm.id])); // Delete selected alarms
    setSelectedAlarms({}); // Clear selected alarms
    setShowCheckboxes(false); // Hide checkboxes
    setAlarmMessage("Selected alarms have been deleted.");

    // Clear the alarm message after 5 seconds
    setTimeout(() => {
      setAlarmMessage("");
    }, 5000);
  };

  const handleDeleteClick = () => {
    if (alarms.length === 0) {
      setAlarmMessage("No alarms are available for deletion.");
      setTimeout(() => {
        setAlarmMessage("");
      }, 3000);
    } else if (showCheckboxes) {
      deleteSelectedAlarms(); // If checkboxes are shown, delete selected alarms
    } else {
      setShowCheckboxes(true); // Show checkboxes for selection
    }
  };

  const sortedAlarms = [...alarms].sort((a, b) => calculateRemainingTime(a) - calculateRemainingTime(b));

  return (
    <div className="container">
      {alarmMessage && <div className="notification">{alarmMessage}</div>}
      <h2>Alarm Clock</h2>
      <div className="timeDisplay">
        <h4>
          {currentTime.toLocaleTimeString([], {
            hour: "2-digit",
            minute: "2-digit",
            hour12: true,
          }).split(" ").map((part, index) => (
            index === 1 ? (
              <span key={index} className="ampm">{part}</span> // Apply the ampm class to the AM/PM text
            ) : (
              <span key={index} className="time">{part}</span> // Default span for the time
            )
          ))}
        </h4>
        <div style={{ display: 'flex', gap: '10px', justifyContent: 'space-between' }}>
          <div
            onClick={() => setShowForm(true)}
            style={{ cursor: "pointer", position: "relative" }}
          >
            <img src={add} alt="Add Alarm" width="50" height="50" />
          </div>
          <div
            onClick={handleDeleteClick} // Use the handleDeleteClick function
            style={{ cursor: "pointer", position: "relative" }}
          >
            <img src={Delete} alt="Delete Selected Alarms" width="50" height="50" />
          </div>
        </div>
      </div>
      {showForm && (
        <div>
          <input
            type="time"
            value={alarmTime}
            onChange={(e) => setAlarmTime(e.target.value)}
            className="input"
          />
          <button onClick={handleSetAlarm} className="button">
            Set Alarm
          </button>
        </div>
      )}
      {sortedAlarms.length > 0 ? (
        <div className="alarm-list">
          <ul>
            {sortedAlarms.map((alarm) => (
              <li
                key={alarm.id}
                className="glassy-effect"
                style={{
                  display: "flex",
                  justifyContent: "space-between",
                  alignItems: "center",
                  margin: "10px 0",
                }}
              >
                <div
                  style={{
                    flex: 1,
                    textAlign: "left",
                    padding: "0px 100px 0px 0px",
                  }}
                >
                  <div
                    style={{
                      fontSize: "1.5em",
                      display: "flex",
                      alignItems: "baseline",
                    }}
                  >
                    <span>{alarm.time12.split(" ")[0]}</span>{" "}
                    <span style={{ fontSize: "0.4em", marginLeft: "5px" }}>
                      {alarm.time12.split(" ")[1]}
                    </span>{" "}
                  </div>
                  <div style={{ fontSize: "0.6em", marginTop: "2px" }}>
                    {alarm.isActive
                      ? `Alarm in ${Math.floor(calculateRemainingTime(alarm) / (1000 * 60 * 60))}h ${Math.floor((calculateRemainingTime(alarm) % (1000 * 60 * 60)) / (1000 * 60))}m ${Math.floor((calculateRemainingTime(alarm) % (1000 * 60)) / 1000)}s`
                      : "Alarm Disabled"}
                  </div>
                </div>
                <div style={{ display: 'flex', alignItems: 'center' }}>
                  {showCheckboxes ? (
                    <input
                      type="checkbox"
                      checked={selectedAlarms[alarm.id] || false}
                      onChange={() => toggleCheckbox(alarm.id)}
                      className="checkbox"
                    />
                  ) : (
                    <button onClick={() => toggleAlarm(alarm.id)} className="button">
                      {alarm.isActive ? "Disable" : "Enable"}
                    </button>
                  )}
                </div>
              </li>
            ))}
          </ul>
        </div>
      ) : (
        <p>No alarms set.</p>
      )}
    </div>
  );
};

export default Alarm;
