import React, { useState } from 'react';
import Stopwatch from './component/Stopwatch';
import Alarm from './component/Alarm';
import './App.css'; // Import a CSS file for styles

function App() {
  const [showStopwatch, setShowStopwatch] = useState(false); // Initially hide Stopwatch
  const [showAlarm, setShowAlarm] = useState(false); // Initially hide Alarm

  const handleStopwatchClick = () => {
    setShowStopwatch(true); // Show Stopwatch when clicked
    setShowAlarm(false); // Hide Alarm
  };

  const handleAlarmClick = () => {
    setShowAlarm(true); // Show Alarm when clicked
    setShowStopwatch(false); // Hide Stopwatch
  };

  return (
    <div className="app-container">
      <h1 className="app-title">Time Management App</h1>
      <div className="component-container">
        {/* Show the Stopwatch or Alarm based on the state */}
        {!showStopwatch && !showAlarm && (
          <div>
            <button onClick={handleStopwatchClick}>Show Stopwatch</button>
            <button onClick={handleAlarmClick}>Show Alarm</button>
          </div>
        )}
        {showStopwatch && <Stopwatch />} {/* Only render Stopwatch if active */}
        {showAlarm && <Alarm />} {/* Only render Alarm if active */}
      </div>
    </div>
  );
}

export default App;
