import React, { useState, useEffect } from "react";
import "./../style/alarm.css";
import add from "./../img/add.svg";
import Delete from "./../img/delete.svg";
import alarm from "./../img/alarm-plus-svgrepo-com.svg";

const Alarm = () => {
  const [alarmTime, setAlarmTime] = useState("");
  const [alarms, setAlarms] = useState([]);
  const [currentTime, setCurrentTime] = useState(new Date());
  const [alarmMessage, setAlarmMessage] = useState("");
  const [showForm, setShowForm] = useState(false);
  const [showCheckboxes, setShowCheckboxes] = useState(false);
  const [selectedAlarms, setSelectedAlarms] = useState({});
  const [showDeleteButton, setShowDeleteButton] = useState(false);
  const [showAlarmClock, setShowAlarmClock] = useState(false);
  const [ringingAlarmId, setRingingAlarmId] = useState(null); // New state for ringing alarm ID

  useEffect(() => {
    const interval = setInterval(() => {
      const now = new Date();
      setCurrentTime(now);
      checkAlarms(now);
    }, 1000);

    return () => clearInterval(interval);
  }, [alarms]);

  const checkAlarms = (now) => {
    setAlarms((prevAlarms) =>
      prevAlarms.map((alarm) => {
        const [alarmHours, alarmMinutes] = alarm.time24.split(":").map(Number);
        const alarmDate = new Date(
          now.getFullYear(),
          now.getMonth(),
          now.getDate(),
          alarmHours,
          alarmMinutes
        );

        if (alarmDate < now) {
          alarmDate.setDate(alarmDate.getDate() + 1);
        }

        if (
          now.getHours() === alarmHours &&
          now.getMinutes() === alarmMinutes &&
          alarm.isActive &&
          ringingAlarmId === null
        ) {
          setAlarmMessage(`Alarm ringing for ${alarm.time12}!`);
          setRingingAlarmId(alarm.id);
          return { ...alarm, isActive: false };
        }

        return alarm;
      })
    );

    setTimeout(() => setAlarmMessage(""), 5000);
  };

  const handleSetAlarm = () => {
    if (alarmTime) {
      const newAlarm = {
        time24: alarmTime,
        time12: formatAlarmTime(alarmTime),
        id: Date.now(),
        isActive: true,
      };
      setAlarms((prev) => [...prev, newAlarm]);
      setAlarmMessage(`Alarm set for ${newAlarm.time12}`);
      setAlarmTime("");
      setShowForm(false);
      setShowDeleteButton(true);

      setTimeout(() => setAlarmMessage(""), 3000);
    } else {
      setAlarmMessage("Please enter a valid time");
      setTimeout(() => setAlarmMessage(""), 3000);
    }
  };

  const snoozeAlarm = () => {
    if (ringingAlarmId) {
      setAlarms((prev) =>
        prev.map((alarm) =>
          alarm.id === ringingAlarmId
            ? {
                ...alarm,
                isActive: true,
                time24: adjustTimeByMinutes(alarm.time24, 5), // Adjust alarm by 5 minutes
              }
            : alarm
        )
      );
      setRingingAlarmId(null);
      setAlarmMessage("Alarm snoozed for 5 minutes.");
      setTimeout(() => setAlarmMessage(""), 3000);
    }
  };

  const stopAlarm = () => {
    if (ringingAlarmId) {
      setRingingAlarmId(null);
      setAlarmMessage("Alarm stopped.");
      setTimeout(() => setAlarmMessage(""), 3000);
    }
  };

  const adjustTimeByMinutes = (time24, minutes) => {
    const [hours, mins] = time24.split(":").map(Number);
    const newTime = new Date();
    newTime.setHours(hours, mins + minutes);
    const newHours = newTime.getHours().toString().padStart(2, "0");
    const newMinutes = newTime.getMinutes().toString().padStart(2, "0");
    return `${newHours}:${newMinutes}`;
  };

  const formatAlarmTime = (time) => {
    const [hours, minutes] = time.split(":").map(Number);
    const ampm = hours >= 12 ? "PM" : "AM";
    const formattedHours = hours % 12 || 12;
    return `${formattedHours}:${minutes < 10 ? "0" + minutes : minutes} ${ampm}`;
  };

  const sortedAlarms = [...alarms].sort(
    (a, b) => calculateRemainingTime(a) - calculateRemainingTime(b)
  );

  return (
    <div className="container">
      {alarmMessage && (
        <div className="notification">
          {alarmMessage}
          {ringingAlarmId && (
            <div className="ringing-actions">
              <button onClick={snoozeAlarm} className="snooze-button">Snooze</button>
              <button onClick={stopAlarm} className="stop-button">Stop</button>
            </div>
          )}
        </div>
      )}

      <div className="timeDisplay">
        {showAlarmClock && (
          <>
            <h4>{currentTime.toLocaleTimeString([], { hour: "2-digit", minute: "2-digit", second: "2-digit", hour12: true })}</h4>
            {/* Rest of the component */}
          </>
        )}

        {!showAlarmClock && (
          <div onClick={() => setShowAlarmClock(true)} style={{ cursor: "pointer" }}>
            <img src={alarm} alt="Alarm Clock" width="100" height="100" />
          </div>
        )}
      </div>
    </div>
  );
};

export default Alarm;
