import React, { useState, useEffect } from 'react';
import './../style/alarm.css';

const Alarm = () => {
  const [alarmTime, setAlarmTime] = useState('');
  const [alarms, setAlarms] = useState([]); // Array to hold all alarms
  const [currentTime, setCurrentTime] = useState(new Date());
  const [alarmMessage, setAlarmMessage] = useState('');

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentTime(new Date());
    }, 1000);

    return () => clearInterval(interval);
  }, []);

  useEffect(() => {
    checkAlarms();
  }, [currentTime, alarms]);

  const checkAlarms = () => {
    // Format current time to 12-hour format without seconds for comparison
    const formattedCurrentTime = currentTime.toLocaleTimeString([], {
      hour: '2-digit',
      minute: '2-digit',
      hour12: true,
    });

    alarms.forEach((alarm) => {
      // Compare the current time with the alarm time
      if (formattedCurrentTime === alarm.time) {
        alert(`Alarm ringing for ${alarm.time}!`);
        setAlarms((prev) => prev.filter(a => a.time !== alarm.time)); // Remove ringing alarm
      }
    });
  };

  const handleSetAlarm = () => {
    if (alarmTime) {
      const newAlarm = { time: formatAlarmTime(alarmTime), id: Date.now() }; // Create a new alarm object
      setAlarms((prev) => [...prev, newAlarm]); // Add the new alarm to the list
      setAlarmMessage(`Alarm set for ${newAlarm.time}`);
      setAlarmTime(''); // Clear input after setting alarm

      // Clear the alarm message after 1 second
      setTimeout(() => {
        setAlarmMessage('');
      }, 1000);
    } else {
      setAlarmMessage('Please enter a valid time');
    }
  };

  const formatAlarmTime = (time) => {
    const [hours, minutes] = time.split(':');
    const date = new Date();
    date.setHours(hours);
    date.setMinutes(minutes);
    // Return the time in 12-hour format
    return date.toLocaleTimeString([], {
      hour: '2-digit',
      minute: '2-digit',
      hour12: true,
    });
  };

  return (
    <div className="container">
      <h2>Alarm Clock</h2>
      <input
        type="time"
        value={alarmTime}
        onChange={(e) => setAlarmTime(e.target.value)}
        className="input"
      />
      <button onClick={handleSetAlarm} className="button">
        Set Alarm
      </button>
      <div className="timeDisplay">
        <h3>Current Time: {currentTime.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit', second: '2-digit', hour12: true })}</h3>
        {alarms.length > 0 ? (
          <div>
            <h4>Alarms Set:</h4>
            <ul>
              {alarms.map((alarm) => (
                <li key={alarm.id}>
                  {alarm.time}
                </li>
              ))}
            </ul>
          </div>
        ) : (
          <h4>No alarms set.</h4>
        )}
        {alarmMessage && <p className="message">{alarmMessage}</p>}
      </div>
    </div>
  );
};

export default Alarm;
