import React, { useState, useEffect } from 'react';
import './../style/alarm.css';

const Alarm = () => {
  const [alarmTime, setAlarmTime] = useState('');
  const [alarms, setAlarms] = useState([]); // Array to hold all alarms
  const [currentTime, setCurrentTime] = useState(new Date());
  const [alarmMessage, setAlarmMessage] = useState('');

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentTime(new Date());
    }, 1000);

    return () => clearInterval(interval);
  }, []);

  useEffect(() => {
    checkAlarms();
  }, [currentTime, alarms]);

  const checkAlarms = () => {
    const formattedCurrentTime = currentTime.toLocaleTimeString([], {
      hour: '2-digit',
      minute: '2-digit',
      second: '2-digit', // Include seconds in current time format
      hour12: true, // Use 12-hour format
    });

    alarms.forEach((alarm) => {
      if (formattedCurrentTime === alarm.time) {
        alert(`Alarm ringing for ${alarm.time}!`);
        setAlarms((prev) => prev.filter(a => a.time !== alarm.time)); // Remove ringing alarm
      }
    });
  };

  const handleSetAlarm = () => {
    if (alarmTime) {
      const newAlarm = { time: alarmTime, id: Date.now() }; // Create a new alarm object
      setAlarms((prev) => [...prev, newAlarm]); // Add the new alarm to the list
      const formattedAlarmTime = formatAlarmTime(alarmTime);
      setAlarmMessage(`Alarm set for ${formattedAlarmTime}`);
      setAlarmTime(''); // Clear input after setting alarm

      // Clear the alarm message after 1 second
      setTimeout(() => {
        setAlarmMessage('');
      }, 1000);
    } else {
      setAlarmMessage('Please enter a valid time');
    }
  };

  const timeUntilAlarm = (alarmTime) => {
    const now = new Date();
    const alarmDate = new Date(now);
    const [hours, minutes] = alarmTime.split(':');
    alarmDate.setHours(hours);
    alarmDate.setMinutes(minutes);
    alarmDate.setSeconds(0);

    if (alarmDate < now) {
      alarmDate.setDate(alarmDate.getDate() + 1); // If the alarm time is in the past, set for the next day
    }

    const timeDifference = alarmDate - now;
    const hoursLeft = Math.floor((timeDifference % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60));
    const minutesLeft = Math.floor((timeDifference % (1000 * 60 * 60)) / (1000 * 60));

    return `Alarm in ${hoursLeft} hours ${minutesLeft} minutes`;
  };

  const formatAlarmTime = (time) => {
    const [hours, minutes] = time.split(':');
    const date = new Date();
    date.setHours(hours);
    date.setMinutes(minutes);
    return date.toLocaleTimeString([], {
      hour: '2-digit',
      minute: '2-digit',
      hour12: true, // Use 12-hour format
    });
  };

  return (
    <div className="container">
      <h2>Alarm Clock</h2>
      <input
        type="time"
        value={alarmTime}
        onChange={(e) => setAlarmTime(e.target.value)}
        className="input"
      />
      <button onClick={handleSetAlarm} className="button">
        Set Alarm
      </button>
      <div className="timeDisplay">
        <h3>Current Time: {currentTime.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit', second: '2-digit', hour12: true })}</h3>
        {alarms.length > 0 ? (
          <div>
            <h4>Alarms Set:</h4>
            <ul>
              {alarms.map((alarm) => {
                const alarmTimeRemaining = timeUntilAlarm(alarm.time);
                return (
                  <li key={alarm.id}>
                    {formatAlarmTime(alarm.time)} - {alarmTimeRemaining}
                  </li>
                );
              })}
            </ul>
          </div>
        ) : (
          <h4>No alarms set.</h4>
        )}
        {alarmMessage && <p className="message">{alarmMessage}</p>}
      </div>
    </div>
  );
};

export default Alarm;
