import React, { useState, useEffect } from 'react';
import './../style/alarm.css';

const Alarm = () => {
  const [alarmTime, setAlarmTime] = useState('');
  const [alarms, setAlarms] = useState([]); // Array to hold all alarms
  const [currentTime, setCurrentTime] = useState(new Date());
  const [alarmMessage, setAlarmMessage] = useState('');
  const [isAlarmRinging, setIsAlarmRinging] = useState(false);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentTime(new Date());
      checkAlarms(); // Check alarms every second
    }, 1000);

    return () => clearInterval(interval);
  }, [alarms]); // Only depend on alarms

  const checkAlarms = () => {
    const formattedCurrentTime = currentTime.toLocaleTimeString([], {
      hour: '2-digit',
      minute: '2-digit',
      hour12: true,
    });

    alarms.forEach((alarm) => {
      if (formattedCurrentTime === alarm.time && alarm.isActive && !isAlarmRinging) {
        setIsAlarmRinging(true);
        setAlarmMessage(`Alarm ringing for ${alarm.time}!`);
        // Automatically toggle off the alarm after it rings
        setAlarms((prev) =>
          prev.map((a) => (a.id === alarm.id ? { ...a, isActive: false } : a))
        );

        // Clear the alarm message after 5 seconds
        setTimeout(() => {
          setAlarmMessage('');
          setIsAlarmRinging(false);
        }, 5000);
      }
    });
  };

  const handleSetAlarm = () => {
    if (alarmTime) {
      const newAlarm = { time: formatAlarmTime(alarmTime), id: Date.now(), isActive: true };
      setAlarms((prev) => [...prev, newAlarm]);
      setAlarmMessage(`Alarm set for ${newAlarm.time}`);
      setAlarmTime('');

      // Clear the alarm message after 3 seconds
      setTimeout(() => {
        setAlarmMessage('');
      }, 3000);
    } else {
      setAlarmMessage('Please enter a valid time');
    }
  };

  const formatAlarmTime = (time) => {
    const [hours, minutes] = time.split(':');
    const date = new Date();
    date.setHours(hours);
    date.setMinutes(minutes);
    return date.toLocaleTimeString([], {
      hour: '2-digit',
      minute: '2-digit',
      hour12: true,
    });
  };

  const calculateRemainingTime = (alarm) => {
    const [alarmHours, alarmMinutes] = alarm.time.split(':').map(Number);
    const now = new Date();
    const alarmDate = new Date(now.getFullYear(), now.getMonth(), now.getDate(), alarmHours, alarmMinutes);

    if (alarmDate <= now) {
      alarmDate.setDate(alarmDate.getDate() + 1); // Set for next day if the time has passed
    }

    const diff = alarmDate.getTime() - now.getTime();

    if (diff < 0) {
      return "Alarm time is invalid"; // Check if the difference is negative
    }

    const hours = Math.floor(diff / (1000 * 60 * 60));
    const minutes = Math.floor((diff % (1000 * 60 * 60)) / (1000 * 60));
    const seconds = Math.floor((diff % (1000 * 60)) / 1000);

    return `Alarm in ${hours}h ${minutes}m ${seconds}s`; // Format the remaining time
  };

  const toggleAlarm = (id) => {
    setAlarms((prev) =>
      prev.map((alarm) =>
        alarm.id === id ? { ...alarm, isActive: !alarm.isActive } : alarm
      )
    );
  };

  return (
    <div className="container">
      {alarmMessage && <div className="notification">{alarmMessage}</div>}
      <h2>Alarm Clock</h2>
      <input
        type="time"
        value={alarmTime}
        onChange={(e) => setAlarmTime(e.target.value)}
        className="input"
      />
      <button onClick={handleSetAlarm} className="button">
        Set Alarm
      </button>
      <div className="timeDisplay">
        <h3>{currentTime.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit', second: '2-digit', hour12: true })}</h3>
        {alarms.length > 0 ? (
          <div>
            <h4>Alarms Set:</h4>
            <ul>
              {alarms.map((alarm) => (
                <li key={alarm.id}>
                  {alarm.time} - {alarm.isActive ? calculateRemainingTime(alarm) : 'Alarm Disabled'}
                  <label className="switch" style={{ marginLeft: '10px' }}>
                    <input
                      type="checkbox"
                      checked={alarm.isActive}
                      onChange={() => toggleAlarm(alarm.id)}
                    />
                    <span className={`slider ${alarm.isActive ? '' : 'round'}`}></span>
                  </label>
                </li>
              ))}
            </ul>
          </div>
        ) : (
          // Show SVG instead of 'No alarms set' text
          <div>
            <svg width="100px" height="100px" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
              <g id="SVGRepo_bgCarrier" stroke-width="0"></g>
              <g id="SVGRepo_tracerCarrier" stroke-linecap="round" stroke-linejoin="round"></g>
              <g id="SVGRepo_iconCarrier">
                <path d="M11 8C11 7.44772 11.4477 7 12 7C12.5523 7 13 7.44772 13 8V11H16C16.5523 11 17 11.4477 17 12C17 12.5523 16.5523 13 16 13H13V16C13 16.5523 12.5523 17 12 17C11.4477 17 11 16.5523 11 16V13H8C7.44771 13 7 12.5523 7 12C7 11.4477 7.44772 11 8 11H11V8Z" fill="#0F0F0F"></path>
                <path fill-rule="evenodd" clip-rule="evenodd" d="M23 12C23 18.0751 18.0751 23 12 23C5.92487 23 1 18.0751 1 12C1 5.92487 5.92487 1 12 1C18.0751 1 23 5.92487 23 12ZM3.00683 12C3.00683 16.9668 7.03321 20.9932 12 20.9932C16.9668 20.9932 20.9932 16.9668 20.9932 12C20.9932 7.03321 16.9668 3.00683 12 3.00683C7.03321 3.00683 3.00683 7.03321 3.00683 12Z" fill="#0F0F0F"></path>
              </g>
            </svg>
          </div>
        )}
      </div>
    </div>
  );
};

export default Alarm;
