import React, { useState, useEffect } from "react";
import "./../style/alarm.css";
import add from './../img/add.svg';
import Delete from './../img/delete.svg';

const Alarm = () => {
  const [alarmTime, setAlarmTime] = useState("");
  const [alarms, setAlarms] = useState([]);
  const [currentTime, setCurrentTime] = useState(new Date());
  const [alarmMessage, setAlarmMessage] = useState("");
  const [showForm, setShowForm] = useState(false);
  const [showCheckboxes, setShowCheckboxes] = useState(false);
  const [selectedAlarms, setSelectedAlarms] = useState({});
  const [showDeleteButton, setShowDeleteButton] = useState(false); // New state variable

  useEffect(() => {
    const interval = setInterval(() => {
      const now = new Date();
      setCurrentTime(now);
      checkAlarms(now);
    }, 1000);

    return () => clearInterval(interval);
  }, [alarms]);

  // ... other functions remain unchanged ...

  const handleAddClick = () => {
    setShowForm(true); // Show the alarm input form
    setShowDeleteButton(true); // Show the delete button when "Add Alarm" is clicked
  };

  const handleDeleteClick = () => {
    if (alarms.length === 0) {
      setAlarmMessage("No alarms were set for deletion.");
      setTimeout(() => {
        setAlarmMessage("");
      }, 3000);
    } else if (showCheckboxes) {
      deleteSelectedAlarms();
    } else {
      setShowCheckboxes(true);
    }
  };

  const sortedAlarms = [...alarms].sort((a, b) => calculateRemainingTime(a) - calculateRemainingTime(b));

  return (
    <div className="container">
      {alarmMessage && <div className="notification">{alarmMessage}</div>}

      {!showForm && <h2 className="glassy-title">Alarm Clock</h2>}

      <div className="timeDisplay">
        <h4>
          {currentTime.toLocaleTimeString([], {
            hour: "2-digit",
            minute: "2-digit",
            second: "2-digit",
            hour12: true,
          }).split(" ").map((part, index) => (
            index === 1 ? (
              <span key={index} className="ampm">{part}</span>
            ) : (
              <span key={index} className="time">{part}</span>
            )
          ))}
        </h4>
        <div style={{ display: 'flex', gap: '10px', justifyContent: 'space-between' }}>
          <div
            onClick={handleAddClick} // Call handleAddClick instead
            style={{ cursor: "pointer", position: "relative" }}
          >
            <img src={add} alt="Add Alarm" width="50" height="50" />
          </div>
          {showDeleteButton && ( // Conditionally render delete button based on showDeleteButton state
            <div
              onClick={handleDeleteClick}
              style={{ cursor: "pointer", position: "relative" }}
            >
              <img src={Delete} alt="Delete Selected Alarms" width="50" height="50" />
            </div>
          )}
        </div>
      </div>

      {showForm && (
        <div>
          <input
            type="time"
            value={alarmTime}
            onChange={(e) => setAlarmTime(e.target.value)}
            className="input"
          />
          <button onClick={handleSetAlarm} className="button">
            Set Alarm
          </button>
        </div>
      )}

      {sortedAlarms.length > 0 && (
        <div className="alarm-list">
          <ul>
            {sortedAlarms.map((alarm) => (
              <li
                key={alarm.id}
                className="glassy-effect"
                style={{
                  display: "flex",
                  justifyContent: "space-between",
                  alignItems: "center",
                  margin: "10px 0",
                }}
              >
                <div
                  style={{
                    flex: 1,
                    textAlign: "left",
                    padding: "0px 100px 0px 0px",
                  }}
                >
                  <div
                    style={{
                      fontSize: "1.5em",
                      display: "flex",
                      alignItems: "baseline",
                    }}
                  >
                    <span>{alarm.time12.split(" ")[0]}</span>{" "}
                    <span style={{ fontSize: "0.4em", marginLeft: "5px" }}>
                      {alarm.time12.split(" ")[1]}
                    </span>{" "}
                  </div>
                  <div style={{ fontSize: "0.6em", marginTop: "2px" }}>
                    {alarm.isActive
                      ? `Alarm in ${Math.floor(calculateRemainingTime(alarm) / (1000 * 60 * 60))}h ${Math.floor((calculateRemainingTime(alarm) % (1000 * 60 * 60)) / (1000 * 60))}m ${Math.floor((calculateRemainingTime(alarm) % (1000 * 60)) / 1000)}s`
                      : "Alarm Disabled"}
                  </div>
                </div>
                <div style={{ display: 'flex', alignItems: 'center' }}>
                  {showCheckboxes ? (
                    <label className="round-checkbox">
                      <input
                        type="checkbox"
                        checked={selectedAlarms[alarm.id] || false}
                        onChange={() => toggleCheckbox(alarm.id)}
                        style={{ display: "none" }}
                      />
                      <span className="checkmark"></span>
                    </label>
                  ) : (
                    <label className="switch" style={{ marginLeft: "10px" }}>
                      <input
                        type="checkbox"
                        checked={alarm.isActive}
                        onChange={() => toggleAlarm(alarm.id)}
                        style={{ display: "none" }}
                      />
                      <span
                        className={`slider ${alarm.isActive ? "" : "round"}`}
                        style={{ width: "49px", height: "22px" }}
                      ></span>
                    </label>
                  )}
                </div>
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default Alarm;
