import React, { useState, useEffect } from "react";
import Select from "react-select";
import './../style/timezonepicker.css';

const TimeZonePicker = () => {
  const [timeZones, setTimeZones] = useState([]);
  const [selectedTimeZone, setSelectedTimeZone] = useState("");
  const [currentTime, setCurrentTime] = useState("");
  const [currentLocationTime, setCurrentLocationTime] = useState("");

  useEffect(() => {
    const getTimeZones = () => {
      try {
        return Intl.supportedValuesOf("timeZone");
      } catch (err) {
        return [
          "America/New_York",
          "Europe/London",
          "Asia/Tokyo",
          "Australia/Sydney",
          "UTC",
        ];
      }
    };

    // Get the supported time zones and map them
    const supportedZones = getTimeZones().map((zone) => ({
      value: zone,
      label: zone,
    }));

    // Set time zones to state
    setTimeZones(supportedZones);
  }, []);

  useEffect(() => {
    const updateTime = () => {
      const now = new Date();

      // Update current location time
      const optionsCurrent = {
        timeZone: Intl.DateTimeFormat().resolvedOptions().timeZone, // User's current time zone
        hour: "2-digit",
        minute: "2-digit",
        second: "2-digit",
        hour12: false,
      };
      const currentFormatter = new Intl.DateTimeFormat([], optionsCurrent);
      setCurrentLocationTime(currentFormatter.format(now));

      // Update selected time zone time
      if (selectedTimeZone) {
        const optionsSelected = {
          timeZone: selectedTimeZone,
          hour: "2-digit",
          minute: "2-digit",
          second: "2-digit",
          hour12: false,
        };
        const selectedFormatter = new Intl.DateTimeFormat([], optionsSelected);
        setCurrentTime(selectedFormatter.format(now));
      }
    };

    const interval = setInterval(updateTime, 1000);
    return () => clearInterval(interval);
  }, [selectedTimeZone]);

  const handleTimeZoneChange = (selectedOption) => {
    console.log("Selected Option:", selectedOption);
    setSelectedTimeZone(selectedOption.value);
  };

  return (
    <div className="timezone-picker-container">
      <label htmlFor="timezone">Select Time Zone:</label>
      <Select
        id="timezone"
        options={timeZones}
        onChange={handleTimeZoneChange}
        placeholder="Search and select a time zone"
      />
      {selectedTimeZone && (
        <div>
          <h3>Selected Time Zone: {selectedTimeZone}</h3>
          <p>Current Location Time: {currentLocationTime}</p>
          <p>Selected Location Time: {currentTime}</p>
        </div>
      )}
    </div>
  );
};

export default TimeZonePicker;
