import React, { useState } from 'react';
import Stopwatch from './component/Stopwatch';
import Alarm from './component/Alarm';
import './App.css'; // Import a CSS file for styles

function App() {
  const [showStopwatch, setShowStopwatch] = useState(true); // State to control visibility of Stopwatch
  const [showAlarm, setShowAlarm] = useState(false); // State to control visibility of Alarm

  const handleStopwatchClick = () => {
    setShowStopwatch(true);
    setShowAlarm(false);
  };

  const handleAlarmClick = () => {
    setShowAlarm(true);
    setShowStopwatch(false);
  };

  return (
    <div className="app-container">
      <h1 className="app-title">Time Management App</h1>
      <div className="component-container">
        <div
          className={`stopwatch-container ${showStopwatch ? 'active' : ''}`}
          onClick={handleStopwatchClick} // Show Stopwatch when clicked
        >
          {showStopwatch && <Stopwatch />} {/* Only render Stopwatch if active */}
        </div>
        <div
          className={`alarm-container ${showAlarm ? 'active' : ''}`}
          onClick={handleAlarmClick} // Show Alarm when clicked
        >
          {showAlarm && <Alarm />} {/* Only render Alarm if active */}
        </div>
      </div>
    </div>
  );
}

export default App;
