import React, { useState, useEffect } from "react";
import "./../style/alarm.css"; // Ensure your CSS file is correctly linked
import add from './../img/add.svg'; // Import your new SVG
import Delete from './../img/delete.svg'; // Import your delete SVG

const Alarm = () => {
  const [alarmTime, setAlarmTime] = useState("");
  const [alarms, setAlarms] = useState([]); // Array to hold all alarms
  const [currentTime, setCurrentTime] = useState(new Date());
  const [alarmMessage, setAlarmMessage] = useState(""); // Notification message
  const [showForm, setShowForm] = useState(false); // Control visibility of form and title
  const [showCheckboxes, setShowCheckboxes] = useState(false); // Control checkbox display
  const [selectedAlarms, setSelectedAlarms] = useState({}); // Track selected alarms for deletion
  const [showDeleteButton, setShowDeleteButton] = useState(false); // New state for delete button visibility

  useEffect(() => {
    const interval = setInterval(() => {
      const now = new Date();
      setCurrentTime(now);
      checkAlarms(now); // Pass current time to check alarms
    }, 1000);

    return () => clearInterval(interval);
  }, [alarms]); // Only depend on alarms

  // ... (rest of the existing code)

  return (
    <div className="container">
      {alarmMessage && <div className="notification">{alarmMessage}</div>}

      {/* Conditionally hide the "Alarm Clock" text if showForm is true */}
      {!showForm && <h2 className="glassy-title">Alarm Clock</h2>}

      <div className="timeDisplay">
        <h4>
          {currentTime.toLocaleTimeString([], {
            hour: "2-digit",
            minute: "2-digit",
            second: "2-digit", // Include seconds
            hour12: true,
          }).split(" ").map((part, index) => (
            index === 1 ? (
              <span key={index} className="ampm">{part}</span> // Apply the ampm class to the AM/PM text
            ) : (
              <span key={index} className="time">{part}</span> // Default span for the time
            )
          ))}
        </h4>
        <div style={{
          display: 'flex',
          justifyContent: showDeleteButton ? 'space-between' : 'center', // Center until delete button appears
          gap: '10px',
        }}>
          <div
            onClick={() => {
              setShowForm(true); // Show the form
              setShowDeleteButton(false); // Hide delete button initially
            }} // When clicked, hide the "Alarm Clock" text
            style={{ cursor: "pointer", position: "relative" }}
          >
            <img src={add} alt="Add Alarm" width="50" height="50" />
          </div>
          {showDeleteButton && ( // Conditionally render delete button based on showDeleteButton state
            <div
              onClick={handleDeleteClick} // Use the handleDeleteClick function
              style={{ cursor: "pointer", position: "relative" }}
            >
              <img src={Delete} alt="Delete Selected Alarms" width="50" height="50" />
            </div>
          )}
        </div>
      </div>

      {showForm && (
        <div>
          <input
            type="time"
            value={alarmTime}
            onChange={(e) => setAlarmTime(e.target.value)}
            className="input"
          />
          <button onClick={handleSetAlarm} className="button">
            Set Alarm
          </button>
        </div>
      )}

      {sortedAlarms.length > 0 && (
        <div className="alarm-list">
          <ul>
            {sortedAlarms.map((alarm) => (
              <li
                key={alarm.id}
                className="glassy-effect"
                style={{
                  display: "flex",
                  justifyContent: "space-between",
                  alignItems: "center",
                  margin: "10px 0",
                }}
              >
                <div
                  style={{
                    flex: 1,
                    textAlign: "left",
                    padding: "0px 100px 0px 0px",
                  }}
                >
                  <div
                    style={{
                      fontSize: "1.5em",
                      display: "flex",
                      alignItems: "baseline",
                    }}
                  >
                    <span>{alarm.time12.split(" ")[0]}</span>{" "}
                    <span style={{ fontSize: "0.4em", marginLeft: "5px" }}>
                      {alarm.time12.split(" ")[1]}
                    </span>{" "}
                  </div>
                  <div style={{ fontSize: "0.6em", marginTop: "2px" }}>
                    {alarm.isActive
                      ? `Alarm in ${Math.floor(calculateRemainingTime(alarm) / (1000 * 60 * 60))}h ${Math.floor((calculateRemainingTime(alarm) % (1000 * 60 * 60)) / (1000 * 60))}m ${Math.floor((calculateRemainingTime(alarm) % (1000 * 60)) / 1000)}s`
                      : "Alarm Disabled"}
                  </div>
                </div>
                <div style={{ display: 'flex', alignItems: 'center' }}>
                  {showCheckboxes ? (
                    <label className="round-checkbox">
                      <input
                        type="checkbox"
                        checked={selectedAlarms[alarm.id] || false}
                        onChange={() => toggleCheckbox(alarm.id)}
                        style={{ display: "none" }} // Hide the default checkbox
                      />
                      <span className="checkmark"></span>
                    </label>
                  ) : (
                    <label className="switch" style={{ marginLeft: "10px" }}>
                      <input
                        type="checkbox"
                        checked={alarm.isActive}
                        onChange={() => toggleAlarm(alarm.id)}
                        style={{ display: "none" }} // Hide the default checkbox
                      />
                      <span
                        className={`slider ${alarm.isActive ? "" : "round"}`}
                        style={{ width: "49px", height: "22px" }} // Adjust slider size
                      ></span>
                    </label>
                  )}
                </div>
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default Alarm;
