import React, { useState, useEffect } from 'react';
import Select from 'react-select';
import './../style/timezonepicker.css'; // Adjust this path if necessary


const TimeZonePicker = () => {
  const [timeZones, setTimeZones] = useState([]);
  const [selectedTimeZone, setSelectedTimeZone] = useState('');
  const [currentTime, setCurrentTime] = useState('');

  useEffect(() => {
    const getTimeZones = () => {
      try {
        return Intl.supportedValuesOf('timeZone');
      } catch (err) {
        return [
          'America/New_York',
          'Europe/London',
          'Asia/Tokyo',
          'Australia/Sydney',
          'UTC',
        ];
      }
    };

    const zones = getTimeZones().map((zone) => ({
      value: zone,
      label: zone,
    }));
    console.log("Time Zones: ", zones); // Log to check the fetched zones
    setTimeZones(zones);
  }, []);


  useEffect(() => {
    const updateTime = () => {
      if (selectedTimeZone) {
        const now = new Date();
        const options = {
          timeZone: selectedTimeZone,
          hour: '2-digit',
          minute: '2-digit',
          second: '2-digit',
          hour12: true,
        };
        const formatter = new Intl.DateTimeFormat([], options);
        setCurrentTime(formatter.format(now));
      }
    };

    const interval = setInterval(updateTime, 1000);
    return () => clearInterval(interval);
  }, [selectedTimeZone]);

  const handleTimeZoneChange = (selectedOption) => {
    setSelectedTimeZone(selectedOption.value);
  };

  return (
    <div>
      <label htmlFor="timezone">Select Time Zone:</label>
      <Select
        id="timezone"
        options={timeZones}
        onChange={handleTimeZoneChange}
        placeholder="Search and select a time zone"
      />
      {selectedTimeZone && (
        <div>
          <h3>Current Time in {selectedTimeZone}:</h3>
          <p>{currentTime}</p>
        </div>
      )}
    </div>
  );
};

export default TimeZonePicker; // Ensure this is the default export
