import React, { useState, useEffect } from 'react';

const Alarm = () => {
  const [alarmTime, setAlarmTime] = useState('');
  const [isAlarmSet, setIsAlarmSet] = useState(false);
  const [currentTime, setCurrentTime] = useState(new Date());
  const [alarmMessage, setAlarmMessage] = useState('');

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentTime(new Date());
      checkAlarm();
    }, 1000);

    return () => clearInterval(interval);
  }, [alarmTime, isAlarmSet]);

  const checkAlarm = () => {
    if (isAlarmSet) {
      const formattedCurrentTime = currentTime.toLocaleTimeString([], {
        hour: '2-digit',
        minute: '2-digit',
      });
      if (formattedCurrentTime === alarmTime) {
        setAlarmMessage('Alarm ringing!');
        setIsAlarmSet(false);
        setAlarmTime('');
      } else {
        setAlarmMessage('');
      }
    }
  };

  const handleSetAlarm = () => {
    if (alarmTime) {
      setIsAlarmSet(true);
      setAlarmMessage(`Alarm set for ${alarmTime}`);
    } else {
      setAlarmMessage('Please enter a valid time');
    }
  };

  return (
    <div style={styles.container}>
      <h2>Alarm Clock</h2>
      <input
        type="time"
        value={alarmTime}
        onChange={(e) => setAlarmTime(e.target.value)}
        style={styles.input}
      />
      <button onClick={handleSetAlarm} style={styles.button}>
        Set Alarm
      </button>
      <div style={styles.timeDisplay}>
        <h3>Current Time: {currentTime.toLocaleTimeString()}</h3>
        {isAlarmSet && <h4>Alarm Set for: {alarmTime}</h4>}
        {alarmMessage && <p style={styles.message}>{alarmMessage}</p>}
      </div>
    </div>
  );
};

const styles = {
  container: {
    textAlign: 'center',
    padding: '20px',
    fontFamily: 'Arial, sans-serif',
    backgroundColor: '#f8f9fa',
    borderRadius: '8px',
    boxShadow: '0 0 10px rgba(0, 0, 0, 0.1)',
    maxWidth: '400px',
    margin: 'auto',
  },
  input: {
    padding: '10px',
    borderRadius: '5px',
    border: '1px solid #ccc',
    margin: '10px 0',
  },
  button: {
    padding: '10px 15px',
    borderRadius: '5px',
    border: 'none',
    backgroundColor: '#007bff',
    color: '#fff',
    cursor: 'pointer',
  },
  buttonHover: {
    backgroundColor: '#0056b3',
  },
  timeDisplay: {
    marginTop: '20px',
  },
  message: {
    color: 'red',
    marginTop: '10px',
  },
};

export default Alarm;
