import React, { useState } from 'react';
import './../'; // Add your styles here

function ToDoList() {
  const [task, setTask] = useState(''); // To store the current input task
  const [tasks, setTasks] = useState([]); // To store the list of tasks

  // Add a new task to the list
  const addTask = () => {
    if (task.trim() === '') return; // Avoid adding empty tasks
    setTasks([...tasks, { text: task, isCompleted: false }]);
    setTask(''); // Clear the input field after adding
  };

  // Mark a task as completed
  const completeTask = (index) => {
    const newTasks = [...tasks];
    newTasks[index].isCompleted = !newTasks[index].isCompleted;
    setTasks(newTasks);
  };

  // Remove a task from the list
  const removeTask = (index) => {
    const newTasks = tasks.filter((_, i) => i !== index);
    setTasks(newTasks);
  };

  return (
    <div className="todo-container">
      <h1>To-Do List</h1>
      <div className="input-section">
        <input
          type="text"
          placeholder="Enter a task..."
          value={task}
          onChange={(e) => setTask(e.target.value)}
        />
        <button onClick={addTask}>Add Task</button>
      </div>

      <ul className="task-list">
        {tasks.map((task, index) => (
          <li key={index} className={task.isCompleted ? 'completed' : ''}>
            <span onClick={() => completeTask(index)}>{task.text}</span>
            <button onClick={() => removeTask(index)}>Remove</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default ToDoList;
