import React, { useState } from 'react';
import Stopwatch from './component/Stopwatch';
import Alarm from './component/Alarm';
import './App.css'; // Import a CSS file for styles

function App() {
  const [activeComponent, setActiveComponent] = useState(''); // State to track which component is active

  const handleComponentClick = (component) => {
    setActiveComponent(component); // Set the active component when clicked
  };

  return (
    <div className="app-container">
      <h1 className="app-title">Time Management App</h1>
      <div className="component-container">
        <div
          className={`component ${activeComponent === 'stopwatch' ? 'active' : ''}`}
          onClick={() => handleComponentClick('stopwatch')}
        >
          <Stopwatch />
        </div>
        <div
          className={`component ${activeComponent === 'alarm' ? 'active' : ''}`}
          onClick={() => handleComponentClick('alarm')}
        >
          <Alarm />
        </div>
      </div>
    </div>
  );
}

export default App;
